script:
cur_frm.set_query("default_gudang", function(frm) {
	return {
		filters: {
			'is_group': 1,
			'type': 'Warehouse'
		}
	}
});
cur_frm.set_query("default_section", function(frm) {
	return {
		filters: {
			'is_group': 1,
			'type': 'Section',
			'parent': cur_frm.doc.default_gudang
		}
	}
});
cur_frm.set_query("default_location", function(frm) {
	return {
		filters: {
			'is_group': 0,
			'type': 'Location',
			'parent': cur_frm.doc.default_section
		}
	}
});
frappe.ui.form.on('Item', {
	default_location: function(frm){
		frm.set_value("default_warehouse", frm.doc.default_location);
	}
})

frappe.ui.form.on('Sales Order Item', {
	item_code: function(frm, cdt, cdn){
		row = locals[cdt][cdn];
		frappe.call({
			"method": "frappe.client.get",
			args: {
				doctype: "Item",
				name: row.item_code
			},
			callback: function (data) {
				frappe.model.set_value(cdt, cdn, "default_gudang", data.message.default_gudang);
				frappe.model.set_value(cdt, cdn, "default_section", data.message.default_section);
				frappe.model.set_value(cdt, cdn, "default_location", data.message.default_location);
			}
		})
	},
	default_location: function(frm, cdt, cdn){
		row = locals[cdt][cdn];
		frappe.model.set_value(cdt, cdn, "warehouse", row.default_location);
	}
})
cur_frm.set_query("default_gudang", "items", function(doc, cdt, cdn) {
	var d = locals[cdt][cdn];
	return {
		filters: [
			['is_group', '=', 1]
		]
	}
});
cur_frm.set_query("default_section", "items", function (doc, cdt, cdn) {
	var row = locals[cdt][cdn];
	return {
		filters: {
			'is_group': 1,
			'type': 'Section',
			'parent': row.default_gudang
		}
	}
});
cur_frm.set_query("default_location", "items", function (doc, cdt, cdn) {
	var row = locals[cdt][cdn];
	return {
		filters: {
			'is_group': 0,
			'type': 'Location',
			'parent': row.default_section
		}
	}
});
